mapper.update:

UPDATE ATTENDANCE SET
    STATUS = #{status}
WHERE ATTENDANCE_DATE BETWEEN to_date(to_char(sysdate,'YYYY-MM-DD'), 'YYYY-MM-DD') AND TO_DATE(to_char(sysdate,'YYYY-MM-DD'), 'YYYY-MM-DD') + 1 
AND USER_ID = #{userId}

mapper.select:

SELECT COUNT(USER_ID) AS "count"
FROM ATTENDANCE
WHERE USER_ID = #{userId}
